import React, { Component, version } from  "react";
import { Col, Row, Container } from "../Grid";
import Card from "../Card";

//import { Button, Input, Footer};
import directoryCatalog from "./directoryCatalog.json";

class Sort extends Component {

    state = {
        sort : "asc",
        filter : "",
        directoryCatalog : [...directoryCatalog]
    }


    renderEmployee = () => {
        const { directoryCatalog } = this.state;
        return directoryCatalog.map( (employee, index)=>
        <Card
            key ={index}
            name = {employee.name}
            title = {employee.title}
            phone = {employee.phone}
            email = {employee.email}
            eDuration = {employee.eDuration}
        />
        );
    }

    handleChange = event => {
        this.setState({ filter: event.target.value });
      };

    onSort = sortType =>{
        this.setState({sortType});
    }
    render() {

        // const {filter, directoryCatalog }

        const {directoryCatalog, sortType} = this.state;

        const sorted = directoryCatalog.sort( (a,b) =>{

            const isReversed = (sortType === "asc") ? 1 : -1;
            return isReversed * a.name.localeCompare(b.name)
        });

        return (  
           <Row>
           <button className="button" onClick={()=>this.onSort("asc")}>Sort Names Ascending</button>
           <button className="button" onClick={()=>this.onSort("desc")}>Sort Names Descending</button>
           <form>
               onChange={handleChange}
               type="text"
               placeholder="Search Employee by Name"
           </form>
                <Row>
               {this.renderEmployee()}
               </Row>
           </Row>
        );
    }
}

export default Sort;
