import React from 'react';
import { Component } from 'react';
import Card from "../Card";
import { Col, Row, Container } from "../Grid";
import SearchButton from '../SeachButton';
import directoryCatalog from "./directoryCatalog.json";

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = { employees: directoryCatalog, searchUser: '' };
  }

  handleInput = e => {
    console.log(e.target.value);
    this.setState({ searchUser: e.target.value });
  };

  render() {
    let filteredUsers = this.state.users.filter(user => {
      return user.name
        .toLowerCase()
        .includes(this.state.searchUser.toLowerCase());
    });
    return (
      <SearchButton handleInput={this.handleInput} />
    );
  }
}

export default Search;
